			window.status = "Playing " + Geraa + " ... (" + MoQreeNameGlobal + ") ... " + "����"
			window.setTimeout("getPosition()", 10);
		} else {
			NSCom.controls.pause();
			window.status = "Done ...";
			return false;
		}
	} else if (roundit(NSCom.controls.CurrentPosition, 3) > roundit(EndPosition, 3)) {
			FlagStop=true;
			NSCom.controls.pause();
			window.status = "Done ...";
			return false;
	} else {
		NSCom.settings.volume = 0;
		NSCom.controls.CurrentPosition = roundit(StartPosition, 3);
		window.status = "Buffering ...";
		window.setTimeout("getPosition()", 1000);
	}
}




