function getPosition() {
	var NSCom = Dom.getElm("NSPlay2")
		if ((roundit(NSCom.controls.CurrentPosition, 3) >= roundit(StartPosition, 3)) && (roundit(NSCom.controls.CurrentPosition, 3) <= roundit(EndPosition, 3))) {
		NSCom.settings.volume = 70;
		if (!FlagStop) {
